   select * from member where id = #{id}   
   
   
   
   select * from member   
   

	
   
   insert into member values
   (#{id},#{passwd},#{name},#{birth},#{phone},#{gender})
   
   
   
   delete from member where id = #{id}
   

   
   update member set 
   passwd = #{passwd},
   name = #{name},
   birth = #{birth},
   phone = #{phone},
   gender = #{gender}
   where id = #{id}
